      const defData = bench.default.map(r => r.auc);
      const familyAuc = this.modelMetrics?.model_families?.churn ? Object.entries(this.modelMetrics.model_families.churn).map(([fam, hv]) => ({
        family: fam,
        auc: hv?.["6m"]?.roc_auc || 0
      })) : [];
      const familyAucDefault = this.modelMetrics?.model_families?.default ? Object.entries(this.modelMetrics.model_families.default).map(([fam, hv]) => ({
        family: fam,
        auc: hv?.["6m"]?.roc_auc || 0
      })) : [];
      return {
        churn: {
          labels: churnLabels,
          datasets: [{
            label: "Churn single-feature AUC",
            data: churnData,
            backgroundColor: "#0ea5e9"
          }]
        },
        default: {
          labels: defLabels,
          datasets: [{
            label: "Default single-feature AUC",
            data: defData,
            backgroundColor: "#f59e0b"
          }]
        },
        families: {
          labels: familyAuc.map(r => r.family),
          datasets: [{
            label: "Churn AUC by family (6m)",
