    buildSurfaceSlices() {
      const surf = this.modelMetrics?.surfaces || {};
      const build = (obj, color) => {
        if (!obj?.x || !obj?.y || !obj?.z) return undefined;
        const datasets = obj.y.slice(0, 3).map((yVal, idx) => ({
          label: `${obj.y_label || "y"}=${yVal.toFixed(1)}`,
          data: obj.z[idx],
          borderColor: color,
          backgroundColor: "rgba(0,0,0,0)",
          tension: 0.15,
          fill: false
        }));
        return {
          labels: obj.x,
