    buildSelectionCurves(problem, key) {
      const sel = this.modelMetrics?.model_selection?.[problem]?.[key];
      if (!sel) return {
        datasets: []
      };
      const params = sel.params || [];
      const toPoints = arr => (arr || []).map((y, idx) => ({
        x: params[idx] ?? idx,
        y
      }));
      return {
        datasets: [{
          label: "Train AUC",
          data: toPoints(sel.train || []),
          borderColor: "#22c55e",
          tension: 0.15,
          fill: false
        }, {
          label: "Val AUC",
          data: toPoints(sel.val || []),
          borderColor: "#f59e0b",
          tension: 0.15,
          fill: false
        }, {
          label: "CV AUC",
          data: toPoints(sel.cv || []),
          borderColor: "#6366f1",
          tension: 0.15,
          fill: false
        }]
      };
    }
    selectionSeedTable() {
      const sel = this.modelMetrics?.model_selection?.churn?.rf_estimators;
      const seeds = this.modelMetrics?.model_selection?.churn?.seeds || [1, 7, 21];
      if (!sel?.seed_runs) return {
        seeds: [],
        rows: []
      };
      const rows = sel.seed_runs.map((run, idx) => ({
        param: sel.params?.[idx],
        scores: run || [],
        mean: run && run.length ? run.reduce((a, b) => a + b, 0) / run.length : 0
      }));
      return {
        seeds,
        rows
      };
    }
    missingnessGrid(kind) {
