    buildRocExplorer() {
      const thresholds = this.thresholdsFor("churn");
      const points = thresholds.map(t => ({
        x: t.fpr,
        y: t.tpr
      }));
      const scenarioPts = [0.33, 0.5, 0.66].map(thr => {
        const nearest = thresholds.reduce((prev, curr) => Math.abs(curr.threshold - thr) < Math.abs((prev?.threshold ?? 0) - thr) ? curr : prev, thresholds[0] || {});
        return {
          x: nearest?.fpr || 0,
          y: nearest?.tpr || 0
        };
      });
      return {
        datasets: [{
          label: "Churn ROC",
          data: points,
          borderColor: "#0ea5e9",
          backgroundColor: "rgba(14,165,233,0.2)",
          showLine: true,
          tension: 0.1,
          fill: false
        }, {
          label: "Scenario thresholds",
          data: scenarioPts,
          borderColor: "#f59e0b",
          backgroundColor: "rgba(245,158,11,0.4)",
          showLine: false,
          pointRadius: 5,
          fill: false
        }]
      };
