      const churnB = this.thresholdsForFamily("churn", famB);
      const defA = this.thresholdsForFamily("default", famA);
      const defB = this.thresholdsForFamily("default", famB);
      this.compareRoc = {
        datasets: [{
          label: `Churn ${famA}`,
          data: churnA.map(t => ({
            x: t.fpr,
            y: t.tpr
          })),
          borderColor: "#19c3b1",
          fill: false,
          tension: 0.1
        }, {
          label: `Churn ${famB}`,
          data: churnB.map(t => ({
            x: t.fpr,
            y: t.tpr
          })),
          borderColor: "#f97316",
          fill: false,
          tension: 0.1
        }, {
