import { __decorate } from "tslib";
import { Component } from "@angular/core";
let CalculatorComponent = class CalculatorComponent {
  constructor() {
    this.investment = 1_000_000;
    this.annualRate = 10; // percent
    this.termMonths = 12;
    this.reinvest = true;
    this.monthlyPayout = false;
    this.projections = [];
    this.summary = {
      monthlyIncome: 0,
      annualIncome: 0,
      totalInterest: 0,
      endingBalance: 0
    };
