    let totalInterest = 0;
    const points = [];
    for (let m = 1; m <= months; m++) {
      const interest = balance * monthlyRate;
      totalInterest += interest;
      if (this.reinvest) {
        balance += interest;
      }
      points.push({
        month: m,
        balance,
        interestEarned: interest
      });
    }
    // Monthly payout assumes no reinvest; use current settings to show cash flow
    const monthlyIncome = principal * monthlyRate;
    const annualIncome = principal * r;
    this.projections = points;
    this.summary = {
      monthlyIncome: this.monthlyPayout ? monthlyIncome : points.at(-1)?.interestEarned || monthlyIncome,
      annualIncome,
      totalInterest,
      endingBalance: balance
    };
  }
};
